import React, {useState, useRef} from 'react'
import './Card.css'

function Card(){
    const dragItem = useRef();
    const dragNode = useRef();

    const handleDragStart = (e, params) => {
        console.log('Drag starting...')
        dragNode.current = e.target;
        dragItem.current = params.offsetLeft;
    }

    return(
        <div draggable onDragStart={(e) => {handleDragStart(e, e.target)}} className="card">
            <h4>First Task</h4>
            <hr></hr>
            <h5>30.05.21 / 12:45</h5>
            <p>Greg</p>
        </div>
    )
}

export default Card;
