import React, {Component} from 'react';
import {Draggable} from 'react-beautiful-dnd';
import './Card.css';

class Card extends Component{
    constructor(){
        super();
        this.state = {
            isClicked: false,
        }
    }

    onClick = () => {
        if(this.state.isClicked){
            this.state.isClicked = false;
        } else {
            this.state.isClicked = true;
        }

    }

    render(){
        return(
            <Draggable draggableId={this.props.task.id}
                index={this.props.index}>
                    {(provided, snapshot) => (
                        <div className={this.state.isClicked ? "testcard" : "card"} 
                        {...provided.draggableProps}
                        {...provided.dragHandleProps}
                        ref={provided.innerRef}
                        isDragging={snapshot.isDragging}>
                            <h4 onClick={this.onClick}>{this.props.task.title}</h4>
                            <hr></hr>
                            <h5>Date and time created: <br/>{this.props.task.datetime}</h5>
                            <p>User: {this.props.task.user}</p>
                            <p class="task-description">{this.props.task.description}</p>
                        </div>
                    )}
                </Draggable>
        )
    }
}

export default Card;
