import React, {useState, useRef} from 'react'
import './Card.css'

function Card(){
    const [dragging, setDragging] = useState(false);

    const dragItem = useRef();
    const dragNode = useRef();
    console.log(dragItem);
    const handleDragStart = (e, params) => {
        console.log('Drag starting...')
        dragNode.current = e.target;
        dragItem.current = params.offsetLeft;
        dragNode.current.addEventListener('dragend', handleDragEnd);
        setDragging(true);
    }

    const handleDragEnd = () => {
        console.log('Ending drag...')
        setDragging(false);
        dragNode.current.removeEventListener('dragend', handleDragEnd);
        dragItem.current = null;
        dragNode.current = null;
    }

    const handleDragEnter = (e, params) => {
        console.log('Drag overrrr now.', params.offsetLeft)
        const currentItem = dragItem.current;
        if(e.target !== dragNode.current){
            console.log("target is not the same!")
        }
    }

    return(
        <div draggable onDragEnter={ dragging ? (e) => {handleDragEnter(e, e.target)} : null} onDragStart={(e) => {handleDragStart(e, e.target)}} className="card">
            <h4>First Task</h4>
            <hr></hr>
            <h5>30.05.21 / 12:45</h5>
            <p>Greg</p>
        </div>
    )
}

export default Card;
